    constructor(props) {
        super(props);
        this.state = {
          fur: 'Notspec',
          question: 1,
          pet: "Notspec",
          submitted: false
      };
        this.changeQuestion = this.changeQuestion.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

      changeQuestion(field, value) {

        if(field === "fuzzy") {
        this.setState({fur: value});

        } else if (field === "pet") {
       this.setState({pet: value});

      } else {
      console.log(value);
       };

  };

  handleSubmit(e){
    e.preventDefault();
    console.log(e.target);
    console.log(this.state);
    this.setState({
        submitted: true 
    })
  }

  endForm(){
    const newV = true
    this.setState({
        submitted: newV 
    })
}



    render(){
        let submit;
        if (this.state.pet !== "Notspec"
        ){
            submit = <Dogs end ={this.endForm}pet = {this.state.pet}/>
        }
        else submit = <div></div>
        if(this.state.submitted === false)
        {return (<div className= 'form' >
        <form onSubmit={this.handleSubmit}>
            {questions.map(question =>  <RenderQuestion  changeQuestion = {this.changeQuestion} number = {this.state} question = {question}/>)}
            <br/>
        </form> 
        {submit}
        </div>)}
        else {
            return <Ending pet = {this.state.pet}/>
        }
    }
};

export default Test;
