import React from 'react';

let questions = [
    { title : 'Do you like warm and fuzzy pets?',
     display: true,
     type: "radio",
     name: "fuzzy",
     options: [{
         idAndValue: 'fuzzy',
         label: "Yes"
     }, {
         idAndValue: 'nofuz',
         label: "No"
     }]},
     {title : 'What kind?',
     display: false,
     type: "radio",
     name: "pet",
     options: [{
         idAndValue: 'dog',
         label: "Dog"
     }, {
         idAndValue: 'hamster',
         label: "Hamster"
     }, 
     {
         idAndValue: 'cat',
         label: "Cat"
     }]},{
     title : 'What kind?',
     display: false,
     type: "radio",
     name: "pet",
     options: [{
         idAndValue: 'pig',
         label: "Pig"
     }, {
         idAndValue: 'lizard',
         label: "Lizard"
     }, 
     {
         idAndValue: 'snake',
         label: "Snake"
     }]},
     {
         title: 'What is your name?',
         display: false,
         type: 'text',
         name: 'name',
         options: [{
         idAndValue: 'name',
         label: "Name"
         }]
     },
     {
         title: 'What is your email?',
         display: false,
         type: 'text',
         name: 'email',
         options: [{
         idAndValue: 'emal',
         label: "Email"
         }]
     }
 ];

 class RenderQuestionOption extends React.Component {
     render (){
        return (<div>
        <input type={this.props.question.type} id={this.props.option.idAndValue} name={this.props.question.name} value={this.props.option.idAndValue} onClick = {this.changeQuestion} />
        <label for={this.props.option.idAndValue}>{this.props.option.label}</label> 
        </div>)
     }
 }
 class RenderQuestionTitle extends React.Component {
    render (){
        return (<p>{this.props.question.title}</p>)
    }
}


class Test extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          fur: true,
          question: 1,
          pet: "dog",
          email: "tranqvilier@gmail.com",
          name: "Sol"
      };
        //this.changeQuestion = this.changeQuestion.bind(this);
        //this.changeFur = this.changeFur.bind(this);
        //this.changePet = this.changePet.bind(this);
        //this.changeMail = this.changeMail.bind(this);
        //this.changeName = this.changeName.bind(this);
      }

      changeQuestion = () => {
        let qn = this.state.question + 1;
        this.setState({question: qn});
  };

    render(){
        return (
        <form method="post" action="http://server1">
            questions.map(question =>  <RenderQuestionTitle question= {question}/>)
            <br/>
            <input type="submit" value="Submit"/>
        </form> );
    }
};

export default Test;
