import React, { Component } from 'react';
import { Line, Bar, Pie } from 'react-chartjs-2';
import { defaults } from 'react-chartjs-2';
import { render } from '@testing-library/react';

const apiKey = 'H02HDVYAO5KH6A0E';

function getStockdata(crypto ,currency) {
    const url = 'https://www.alphavantage.co/query?function=DIGITAL_CURRENCY_DAILY&symbol='+crypto+'&market='+currency+'&apikey=' + apiKey
    fetch(url).then(response => {
        if (response.ok) {
          return response.json()
        } 
          throw new Error('Request failed!');

      }, networkError => {
        console.log(networkError.message)
      }).then(jsonResponse=>{
        console.log(jsonResponse);
        return jsonResponse
      })};

class Infograph extends Component {
constructor (props) {
    super(props);
    this.state = {
        chartData: {
           labels:  ['today', 'yesterday', 'the day before'],
           datasets: [{
            label: 'UV index',
            data: [300, 400, 350],
           },
           {
            label: 'SNP point Change',
            data: [700, 500, 450],
           } ]
        }
    }
}
render() {
    getStockdata('ETH','USD');
    return (
        <div className="chart">
            <Line 
                data= {this.state.chartData}

                options = {{
                    title: {
                        display: true,
                        text: 'SNP points vs Sunlight',
                        fontSize: 25
                    },
                    legend: {
                       display: true,
                       position: 'left'
                    }
                }}


            />
        </div>
    )
}
};

export default Infograph;
