class RenderWalk extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            city : 'Riga',
            venues : [],
            forecast : {},
            searched: false,
            pic: ''
      };
      this.handleData=this.handleData.bind(this);
      this.handleClick=this.handleClick.bind(this);
      this.handleEnter=this.handleEnter.bind(this);
