import React from 'react';
import './story.css';
import './me.jpg';
import './expectation.png';

class MyBio extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      lePic: './expectation.png',
      clicked: false
  };
    this.changePhoto = this.changePhoto.bind(this);
    this.changePhotoBack = this.changePhotoBack.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }
  changePhoto = () => {
    this.setState({lePic: './me.jpg'});
  }

  changePhotoBack = () => {
    this.setState({lePic: './expectation.png'});
  }

  handleClick = () => {
    this.setState({lePic: './me.jpg', clicked: true});
  }

  render() {
    let lePic = this.state.lePic;
    const Title = 'Hire good programmer in Riga';
    return  (<div><div className="story">
    <h2>Hi! My name's Sol and I'm your dream programmer</h2>
    <p>Even if you don't dream about awesome programmers at night you probably want someone who:</p>
    <ul>
        <li>Can build you a sophisticated website</li>
        <li>Understands SEO and Marketing</li>
        <li>Will turn your business goals into a reality</li>
        <li>Is super easy to deal with. No nerd-speak. Unless you want to ;)</li>
    </ul>
        <img  className="photo" onClickCapture = {this.handleClick} onMouseEnter={this.changePhoto} onMouseLeave = {this.changePhotoBack} 
     src = {lePic}
     alt = {Title}
    />
  </div> </div>);
  }
};




export default MyBio;
