import React from 'react';

let questions = [
    { title : 'Do you like warm and fuzzy pets?',
     display: true,
     type: "radio",
     name: "fuzzy",
     id: 1,
     options: [{
         idAndValue: 'yes',
         label: "Yes"
     }, {
         idAndValue: 'no',
         label: "No"
     }]},
     {title : 'What kind?',
     display: false,
     type: "radio",
     name: "pet",
     id: 2,
     options: [{
         idAndValue: 'dog',
         label: "Dog"
     }, {
         idAndValue: 'hamster',
         label: "Hamster"
     }, 
     {
         idAndValue: 'cat',
         label: "Cat"
     }]},{
     title : 'What kind?',
     display: false,
     type: "radio",
     name: "pet",
     id: 3,
     options: [{
         idAndValue: 'pig',
         label: "Pig"
     }, {
         idAndValue: 'lizard',
         label: "Lizard"
     }, 
     {
         idAndValue: 'snake',
         label: "Snake"
     }]},
     {
         title: 'What is your name?',
         display: false,
         type: 'text',
         name: 'name',
         id: 4,
         options: [{
         idAndValue: 'name',
         label: "Name"
         }]
     },
     {
         title: 'What is your email?',
         display: false,
         type: 'text',
         name: 'email',
         id: 5,
         options: [{
         idAndValue: 'emal',
         label: "Email"
         }]
     }
 ];

 class RenderQuestionOption extends React.Component {

    handleChange(e) {
        console.log(e.target.value);
        console.log(e.target.name);
        console.log(e.target);
        const field = e.target.value;
       const value = e.target.name;
       this.props.onClick(field,value);
      }
     render (){
         let option = this.props.option;
         let question = this.props.question;
         let field = question.name;
         let value = option.idAndValue;
        return (<div>
          <input key = {question.id} onClick = {this.handleChange} type={question.type} id={option.idAndValue} name={question.name} value={option.idAndValue}  />
         <label for={option.idAndValue}>{option.label}</label> 
        </div>)
     }
 };

 class RenderOptions extends React.Component {
    render () {
        return (<div> 
            {this.props.options.options.map(option => <RenderQuestionOption onClick = {this.props.onClick} question = {this.props.options} option = {option}/>)}
            </div>
            )

    }
 };
 class RenderQuestion extends React.Component {
    render (){
        if (this.props.number.fur === "Notspec" && this.props.question.id === 1){
            console.log(this.props.number);
        return (<div>
        <p>{this.props.question.title}</p>
            <RenderOptions onClick = {this.props.onClick} options = {this.props.question}/>
            </div>
            )} else if (this.props.number.fur === "yes" && this.props.question.id === 2){
                return <div>
                <p>{this.props.question.title}</p>
                    <RenderOptions onClick = {this.props.onClick} options = {this.props.question}/>
                    </div>
            }
         else if (this.props.number.fur === "no" && this.props.question.id === 3){
            return <div>
            <p>{this.props.question.title}</p>
                <RenderOptions onClick = {this.props.onClick} options = {this.props.question}/>
                </div>
        } else if (this.props.number.pet !== "Notspec" && this.props.question.id >3 ){
            return <div>
            <p>{this.props.question.title}</p>
                <RenderOptions onClick = {this.props.onClick} options = {this.props.question}/>
                </div>
        } else {
            return <div></div>
        }

      }

};


class Test extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          fur: 'Notspec',
          question: 1,
          pet: "Notspec",
          email: "Notspec",
          name: "Notspec"
      };
        this.changeQuestion = this.changeQuestion.bind(this);
        this.changeContacts = this.changeContacts.bind(this);
      }

      changeQuestion = (field, value) => {
        console.log(field);
        console.log(value);
        //if(field === "fuzzy") {
        //this.setState({fur: value});
        console.log(this.state.fur);
        //} else if (field === "pet") {
       // this.setState({pet: value});
       // console.log(value);
      //  } else {
         //   console.log(value);
        //};

  };



  changeContacts = (name,email) => {
    this.setState({
        name: name,
        email: email
    });
  }

    render(){
        return (<div>
        <form method="post" action="http://server1">
            {questions.map(question =>  <RenderQuestion onClick = {this.changeQuestion()} number = {this.state} question = {question}/>)}
            <br/>
            <input type="submit" value="Submit"/>
        </form> </div>);
    }
};

export default Test;
