import React from 'react';

class Test extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          fur: true,
          question: 1,
          pet: "dog",
          email: "tranqvilier@gmail.com",
          name: "Sol"
      };
        //this.changeQuestion = this.changeQuestion.bind(this);
        //this.changeFur = this.changeFur.bind(this);
        //this.changePet = this.changePet.bind(this);
        //this.changeMail = this.changeMail.bind(this);
        //this.changeName = this.changeName.bind(this);
      }

      changeQuestion = () => {
        let qn = this.state.question + 1;
        this.setState({question: qn});
  }
    questions = [
       { title : <p>Do you like warm and fuzzy pets?</p>,
        option1: <div><input type="radio" id="fuzzy" name="fuzzy" value="fuzzy"
         onClick = {this.changeQuestion} />
            <label for="fuzzy">Yes</label> </div> ,
        option2: <div> <input type="radio" id="nofuz" name="fuzzy" value="nofuz" onClick = {this.changeQuestion}
         />
            <label for="cat">No</label></div>}
    ];
    render(){
        return (
        <form method="post" action="http://server1">
            {questions[1]}
            <p>What kind?</p>
            <input type="radio" id="dog" name="pet" value="dog"
          onClick = {this.changeQuestion}/>
            <label for="fuzzy">Dogs</label>
            <input type="radio" id="hams" name="pet" value="hams"
          onClick = {this.changeQuestion} />
            <label for="notfuzzy">Hamsters</label>
            <input type="radio" id="cat" name="pet" value="cat"
          onClick = {this.changeQuestion} />
            <label for="cat">Cats</label>
            <p>What kind?</p>
            <input type="radio" id="pig" name="pet" value="pig"
         onClick = {this.changeQuestion} />
            <label for="fuzzy">Pigglets</label>
            <input type="radio" id="lizard" name="pet" value="lizard"
         onClick = {this.changeQuestion} />
            <label for="notfuzzy">Lizards</label>
            <input type="radio" id="snake" name="pet" value="snake"
         onClick = {this.changeQuestion} />
            <label for="cat">Snakes</label>
            <p>Enter your name:</p>
            <input type="text" name="name"/>
            <br/>
            <p>Enter your email:</p>
            <input type="text" name="email"/>
            <br/>
            <input type="submit" value="Submit"/>
        </form> );
    }
};

export default Test;
