  {
    type: 'igame',
    info: 'Interactive browser game',
    desc: 'This app is a simple browser game'
  },
];

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      graph: '<p className="info">API Data Get and Graph Display</p>',
      mail: '<p className="info">Forms, Tests and Subscriptions</p>',
      park: '<p className="info">API Data Get and List Display</p>',
      igame: '<p className="info">Interactive browser game</p>'

  };
