    this.hideInfo = this.hideInfo.bind(this);
  }




  render() {return (
    <div >

<div className="head">
<h1>Sol Dvortsova</h1>

<p>Hi, I'm starting out as JS programmer. Look what I can do.</p>
</div>
<div className="works" >
{content.map(item => {
  return (<div className={item.type} 
  //onMouseEnter={this.giveInfo(graph)} onMouseLeave = {this.hideInfo(graph)}
