import React, { Component } from 'react';
import { Line} from 'react-chartjs-2';
//import { defaults } from 'react-chartjs-2';
//import { render } from '@testing-library/react';

const apiKey = 'H02HDVYAO5KH6A0E';

const numberNeeded = 20;

const sample = {
    'Meta Data':
    {Information: "Daily Prices and Volumes for Digital Currency",
    'Digital Currency Code': "ETH",
    'Digital Currency Name': "Ethereum",
    'Market Code': "USD",
    'Market Name': "United States Dollar",
    'Last Refreshed': "2020-04-10 00:00:00",
    'Time Zone': "UTC"},
'Time Series (Digital Currency Daily)':
    {'2020-04-10': {'1a. open (USD)': "169.56000000"},
    '2020-04-09': {'1a. open (USD)': "173.15000000"},
    '2020-04-08':{'1a. open (USD)': "164.62000000"}}
};



function getStockdata(crypto ,currency) {
    const url = 'https://www.alphavantage.co/query?function=DIGITAL_CURRENCY_DAILY&symbol='+crypto+'&market='+currency+'&apikey=' + apiKey
    return fetch(url).then(response => {
        if (response.ok) {
          return response.json()
        } 
          throw new Error('Request failed!');

      }, networkError => {
        console.log(networkError.message)
      }).then(jsonResponse=>{
        console.log(jsonResponse);
        return jsonResponse
      })};


class Infograph extends Component {
constructor (props) {
    super(props);
    this.state = {
        chartData: {
           labels:  Object. keys(this.props.data['Time Series (Digital Currency Daily)']).reverse().slice(0,numberNeeded),
           datasets: [{
            label: this.props.data['Meta Data']['Digital Currency Name'],
            data: Object.values(this.props.data['Time Series (Digital Currency Daily)']).map(value => {return value['1a. open (USD)']}).reverse().slice(0,numberNeeded),
           } ]
        }
    }
}

handleClick(e){
    this.props.handleSearch('BTC','USD')}
render() {
    console.log();
  //getStockdata('ETH','USD');
    return (
        <div className="chart">
            <Line 
                data= {this.state.chartData}

                options = {{
                    title: {
                        display: true,
                        text: this.props.data['Meta Data']['Market Name']+'/'+this.props.data['Meta Data']['Digital Currency Name'],
                        fontSize: 25
                    },
                    legend: {
                       display: false,
                       position: 'left'
                    }
                }}


            />
        <button onClick={this.handleClick}>Get data</button>
        </div>
    )
}
};

class Coolchart extends React.Component{
    constructor (props) {
        super(props);
        this.state = {
            data:sample
        }
    this.handleSearch = this.handleSearch.bind(this)
    }
    handleSearch(crypto ,currency){
       getStockdata(crypto ,currency).then(response => this.setState({
           data: response
       }))
    }
    render() {
      return ( <div>
      <Infograph data={this.state.data} search={this.handleSearch}/>
      </div>
      )
    }
}
export default Coolchart;
