import React from 'react';
import './nav.css'

class NavBar extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
          class: 'big'
      };
        this.displayMenu = this.displayMenu.bind(this);
        this.hideMenu = this.hideMenu.bind(this);
        this.switchStyle = this.switchStyle.bind(this)
      }

    displayMenu() {
        this.setState({
            class: 'small'
        })
    }

    hideMenu() {
        this.setState({
            class: 'big'
        })
    }

    switchStyle(){
        if(this.state.style === 'big'){
            this.displayMenu()
        } else {
            this.hideMenu
        }
    }


    render(){
        return (
            <div className='navbar'>
                <h1 >Sol</h1>
                <button onMouseEnter= {this.displayMenu} onClick={this.switchStyle}>Burger</button>
                <ul className = {this.state.class} >
                    <li onClick = {this.hideMenu}>Cool graph I can do with API data</li>
                    <li onClick = {this.hideMenu}>Test to email app</li>
                    <li onClick = {this.hideMenu}>Park app</li>
                    <li onClick = {this.hideMenu}>Awesome game about true love</li>
                </ul>
            </div>
        )
    }
}

export default NavBar;
