import React from 'react';
import {getVenues, getForecast, getPhotos} from './apisearch.js';


class RenderWalk extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            city : 'Riga',
            venues : [],
            forecast : {},
            searched: false,
            pic: ''
      };
      this.handleData=this.handleData.bind(this);
      this.handleClick=this.handleClick.bind(this);
      }
      handleData(e){
        console.log(e.target.value);
        const searchCity = e.target.value;
        this.setState({
            city: searchCity
        });
      }
      handleClick(e){ 
        console.log('searching for ' + this.state.city);

        this.setState({
            searched: false,
              })

        getVenues(this.state.city).then(vResult => {
            if (vResult !== undefined && vResult !== {}){
       this.setState({
           venues : vResult, 
           searched: true,
             })}
            else {alert('Please try entering city again');

        }});

        if (this.state.venues !== []){
        console.log('getting forecast');
        getForecast(this.state.city).then(vResult =>
            this.setState({
                forecast : vResult
                  }));}
        e.preventDefault();

      }

    render(){

        if(this.state.searched) {
            console.log(this.state.venues);
            return <div> 
                <input onChange={this.handleData} />
            <button onClick={this.handleClick}>Search</button>
                <h3>{this.state.city} </h3>
                <RenderVenue  venues={this.state.venues} forecast = {this.state.forecast}/>
            </div>
        } else {
            return (<div>
            <input onChange={this.handleData} />
            <button onClick={this.handleClick}>Search</button>
            </div>)
    }}

};

class RenderPark extends React.Component {
    render(){
        console.log('all parks incoming')
        return (<div>
            {this.props.venues.map(park => {
                const href = 'https://maps.google.com/?q=' +park.location.lat+ ','+ park.location.lng; 
                return <div> 
                <p> {park.name}</p>
                <p> {park.categories[0].name}</p>
                <a href={href} target='_blank' >Directions</a>
                </div>
            })}
        </div>)

}}

class RenderVenue extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            pic: ''
      }
    }

    timeConverter(UNIX_timestamp){
        const a = new Date(UNIX_timestamp * 1000);
        const hour = a.getHours();
        const min = a.getMinutes() < 10 ? '0' + a.getMinutes() : a.getMinutes(); 
        const time = hour + ':' + min;
        return time;
      }

      componentDidMount(){
        console.log('here comes the photo!')
        getPhotos(this.props.venues[0].id).then(pResult =>
            this.setState({
                pic : pResult
                  }));
      }

render(){
    const src = 'https://openweathermap.org/img/wn/'+this.props.forecast.weather[0].icon+'@2x.png';
    const href = 'https://maps.google.com/?q=' +this.props.venues[0].location.lat+ ','+ this.props.venues[0].location.lng;
    const photo = this.props.venues[0].photo;
   // console.log(this.props.venues[0]);

    return <div>
       <img src={src} />
       <p>{this.props.forecast.weather[0].main}</p>
       <p>Temperature: {Math.floor(this.props.forecast.main.temp- 273.15)}C</p>
       <p>Sunset {this.timeConverter(this.props.forecast.sys.sunset)} PM</p>
       <table>
<tbody>
<tr>
<td><img src={this.state.pic} /></td>
<td> 
       <RenderPark venues={this.props.venues}/>



</td>
</tr>
</tbody>
</table>  
    </div>
}
};
export default RenderWalk;
