    console.log(numberNeeded);
    const url = 'https://www.alphavantage.co/query?function=DIGITAL_CURRENCY_DAILY&symbol='+crypto+'&market='+currency+'&apikey=' + apiKey
    return fetch(url).then(response => {
        if (response.ok) {
          return response.json()
