          throw new Error('Request failed!');

      }, networkError => {
        console.log(networkError.message)
      }).then(jsonResponse=>{
       //console.log(jsonResponse);

        const data = 
        {labels: Object. keys(jsonResponse['Time Series (Digital Currency Daily)']).reverse().slice(0,numberNeeded),
        label: jsonResponse['Meta Data']['3. Digital Currency Name'],
        data: Object.values(jsonResponse['Time Series (Digital Currency Daily)']).map(value => {return value['1a. open ('+jsonResponse['Meta Data']['4. Market Code']+')']}).reverse().slice(0,numberNeeded).map(
                value => {return parseFloat(value)}),
        text : jsonResponse['Meta Data']['5. Market Name']+'/'+jsonResponse['Meta Data']['3. Digital Currency Name'],
        }
        return data

      })};


