    }
    handleSearch(crypto ,currency){
      return getStockdata(crypto ,currency).then(response => this.setState({
           data: response,
           display: true
       }))
    }
    render() {
    //console.log(this.state.data);
      return ( <div>
      <Infograph show={this.state.display} data={this.state.data} search={this.handleSearch}/>
      </div>
      )
    }
}
export default Coolchart;
