import React from 'react';
import './game.css';
import Spoink from './spinat';
import Gamefield from './grid';

class Game extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            level: 1,
            affection: 0,
        }
    }

    foodChoise(foodItem){
        const newLevel = this.state.level + 1;
        let newAffection = this.state.affection;
        if(foodItem.fType === 'bad') {
            newAffection = newAffection -1
        } else {
            newAffection = newAffection + 1
        }
        this.setState(
            {
                level: newLevel,
                affection: newAffection
            }
        )
    }

render(){
    return (<div className='game'>
        <Gamefield foodChoise={this.foodChoise}  level={this.state.level} />
        <Spoink affection={this.state.affection} />
        </div>
    )
}

}

export default Game;
