class GetAPIDataForCity extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            city : 'Malta'
      };
      }


    getVenues = async(city)=>{
        const clientId = 'JR4J5UNKZQLHYDALGZA2FO2LUSNZRNKXZ5O5K2TWVST3PEYZ';
        const clientSecret = 'OOEWMJ4EWHA3G1ABVY42QXLIXJ1XGN0UHWUTC05J0HXOOK5H';
        const url = 'https://api.foursquare.com/v2/venues/explore?near=';
        //const city = $input.val();
        const urlToFetch = url + city + '&limit=50&client_id='+ clientId + '&client_secret='+clientSecret+'&v=20200225';
        try{
          const response = await fetch(urlToFetch)
          if (response.ok) {
            const jsonResponse = await response.json();
            const venues = jsonResponse.response.groups[0].items.map(item => item.venue);
            console.log(venues);
            return venues
          } throw new Error('Request failed!')
        }
        catch(error){
          console.log(error)
        }
      };

      getForecast = async(city) => {
        const openWeatherKey = '24baa893cee78ee225eca9d32759df14';
        const weatherUrl = 'https://api.openweathermap.org/data/2.5/weather';
        //const city = $input.val();
        const urlToFetch = weatherUrl +'?q='+city+'&APPID='+openWeatherKey;
        try{
        const response = await fetch(urlToFetch);
          if (response.ok) {
           const jsonResponse = await response.json();
            console.log(jsonResponse);
            return jsonResponse;
          }
      }
        catch(error) {
          console.log(error)
        }
      }



      render(){
          const forecast = this.getForecast(this.state.city);
          const venues =  this.getVenues(this.state.city);
      return <RenderWalk forecast={forecast} venues={venues}/>
      }
};

class RenderWalk extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            forecast : [],
            venues: [],
      };
      this.handleData = this.handleData.bind(this);
      }

handleData(forecast, venue)
      {
        this.setState({
        forecast : forecast,
        venues: venue
      })}

    render(){
        this.handleData(this.state.forecast,this.state.venues);
        return <div>
            <p>Hello</p>
            <p>{this.state.forecast}</p>
            <p>{this.state.venues}</p>        

            </div>
    }

};
export default GetAPIDataForCity;
