import React, { Component } from 'react';
import { Line} from 'react-chartjs-2';
//import { defaults } from 'react-chartjs-2';
//import { render } from '@testing-library/react';

const apiKey = 'H02HDVYAO5KH6A0E';

const numberNeeded = 20;



function getStockdata(crypto ,currency, numberNeeded) {
    console.log(numberNeeded);
    const url = 'https://www.alphavantage.co/query?function=DIGITAL_CURRENCY_DAILY&symbol='+crypto+'&market='+currency+'&apikey=' + apiKey
    return fetch(url).then(response => {
        if (response.ok) {
          return response.json()
        } 
          throw new Error('Request failed!');

      }, networkError => {
        console.log(networkError.message)
      }).then(jsonResponse=>{
       //console.log(jsonResponse);

        const data = 
        {labels: Object.keys(jsonResponse['Time Series (Digital Currency Daily)']).reverse().slice(0,numberNeeded),
        label: jsonResponse['Meta Data']['3. Digital Currency Name'],
        data: Object.values(jsonResponse['Time Series (Digital Currency Daily)']).map(value => {return value['1a. open ('+jsonResponse['Meta Data']['4. Market Code']+')']}).reverse().slice(0,numberNeeded).map(
                value => {return parseFloat(value)}),
        text : jsonResponse['Meta Data']['5. Market Name']+'/'+jsonResponse['Meta Data']['3. Digital Currency Name'],
        }
        return data

      })};


class Infograph extends Component {
constructor (props) {
    super(props);
    this.state = {
        days:7,
        crypto: 'ETH',
        currency: 'EUR'
    }

this.handleClick=this.handleClick.bind(this);

}

handleClick(e){
    this.props.search('ETH','EUR', 20)
}




render() {
    //console.log(Object.values(this.props.data['Time Series (Digital Currency Daily)']).map(value => {return value['1a. open ('+this.props.data['Meta Data']['4. Market Code']+')']}).reverse().slice(0,numberNeeded).map(
    //    value => {return parseFloat(value)}
   // ));
  //  console.log(Object. keys(this.props.data['Time Series (Digital Currency Daily)']).reverse().slice(0,numberNeeded));
  //getStockdata('ETH','USD');
  const data = {
    labels:  this.props.data.labels,
    datasets: [{
        label: this.props.data.label, 
        data: this.props.data.data,
    } ]
}
  if (this.props.show) { 
  return (
        <div className="chart">
            <Line 
                data= {data}

                options = {{
                    title: {
                        display: true,
                        text: this.props.data.text,
                        fontSize: 25
                    },
                    legend: {
                       display: false,
                       position: 'left'
                    }
                }}


            />
            <input type="radio" id="7" name="Days Needed" value="7"></input>
            <label for="7">7</label>
            <button onClick={this.handleClick}>Get data</button>
        </div>)}
    else {return (<div>
        <input type="radio" id="7" name="Days Needed" value="7" defaultChecked></input>
        <label for="7">7</label>
        <input type="radio" id="14" name="Days Needed" value="14"></input>
        <label for="14">14</label>
        <input type="radio" id="30" name="Days Needed" value="30"></input>
        <label for="30">30</label>
        <input type="radio" id="100" name="Days Needed" value="100"></input>
        <label for="100">100</label>
    <button onClick={this.handleClick}>Get data</button>
    </div>
    )}
}
};

class Coolchart extends React.Component{
    constructor (props) {
        super(props);
        this.state = {
            data: {},
            display: false
        }
    this.handleSearch = this.handleSearch.bind(this)
    }
    handleSearch(crypto ,currency, numberNeeded){
      return getStockdata(crypto ,currency,numberNeeded).then(response => this.setState({
           data: response,
           display: true
       }))
    }
    render() {
    //console.log(this.state.data);
      return ( <div>
      <Infograph show={this.state.display} data={this.state.data} search={this.handleSearch}/>
      </div>
      )
    }
}
export default Coolchart;
