        //const city = $input.val();
        const urlToFetch = url + city + '&limit=50&client_id='+ clientId + '&client_secret='+clientSecret+'&v=20200225';
        try{
          const response = await fetch(urlToFetch)
          if (response.ok) {
            const jsonResponse = await response.json();
            const venues = jsonResponse.response.groups[0].items.map(item => item.venue);
            console.log(venues);
            return venues
          } throw new Error('Request failed!')
        }
        catch(error){
          console.log(error)
        }
      };

      getForecast = async(city) => {
        const openWeatherKey = '24baa893cee78ee225eca9d32759df14';
        const weatherUrl = 'https://api.openweathermap.org/data/2.5/weather';
        //const city = $input.val();
        const urlToFetch = weatherUrl +'?q='+city+'&APPID='+openWeatherKey;
        try{
        const response = await fetch(urlToFetch);
          if (response.ok) {
           const jsonResponse = await response.json();
            console.log(jsonResponse);
            return jsonResponse;
          }
      }
        catch(error) {
          console.log(error)
        }
      }

      renderReturns (input) {
          return input

      }

      render(){
          const forecast = this.getForecast(this.state.city).then(forecast =>  this.renderReturns(forecast)) ;
          const venues =  this.getVenues(this.state.city).then(venues => this.renderReturns(venues));
      return <div>
      <p>{forecast}</p>
      <p>{venues}</p>
      </div>
      }
}


export default GetAPIDataForCity;
