import React from 'react';

let questions = [
    { title : 'Do you like warm and fuzzy pets?',
     display: true,
     type: "radio",
     name: "fuzzy",
     id: 1,
     options: [{
         idAndValue: 'yes',
         label: "Yes"
     }, {
         idAndValue: 'no',
         label: "No"
     }]},
     {title : 'What kind?',
     display: false,
     type: "radio",
     name: "pet",
     id: 2,
     options: [{
         idAndValue: 'dog',
         label: "Dog"
     }, {
         idAndValue: 'hamster',
         label: "Hamster"
     }, 
     {
         idAndValue: 'cat',
         label: "Cat"
     }]},{
     title : 'What kind?',
     display: false,
     type: "radio",
     name: "pet",
     id: 3,
     options: [{
         idAndValue: 'pig',
         label: "Pig"
     }, {
         idAndValue: 'lizard',
         label: "Lizard"
     }, 
     {
         idAndValue: 'snake',
         label: "Snake"
     }]},
     {
         title: 'What is your name?',
         display: false,
         type: 'text',
         name: 'name',
         id: 4,
         options: [{
         idAndValue: 'name',
         label: "Name"
         }]
     },
     {
         title: 'What is your email?',
         display: false,
         type: 'text',
         name: 'email',
         id: 5,
         options: [{
         idAndValue: 'emal',
         label: "Email"
         }]
     }
 ];

 class RenderQuestionOption extends React.Component {
     render (){
         let option = this.props.option;
         let question = this.props.question;
        return (<div>
          <input key = {question.id} onClick = {this.props.onClick(question.name, option.idAndValue)} type={question.type} id={option.idAndValue} name={question.name} value={option.idAndValue}  />
         <label for={option.idAndValue}>{option.label}</label> 
        </div>)
     }
 };

 class RenderOptions extends React.Component {
    render () {
        return (<div> 
            {this.props.options.options.map(option => <RenderQuestionOption onClick = {this.props.onClick} question = {this.props.options} option = {option}/>)}
            </div>
            )

    }
 };
 class RenderQuestion extends React.Component {
    render (){
        if (this.props.question.id >= this.props.number){
        return (<div>
        <p>{this.props.question.title}</p>
            <RenderOptions onClick = {this.props.onClick} options = {this.props.question}/>
            </div>
            )} else {
                return <div></div>
            }

      }

};


class Test extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          fur: 'Notspec',
          question: 1,
          pet: "Notspec",
          email: "Notspec",
          name: "Notspec"
      };
        this.changeQuestion = this.changeQuestion.bind(this);
        this.changeContacts = this.changeContacts.bind(this);
      }

      changeQuestion = (field, value) => {
        if(field === "fuzzy") {
        this.setState({fur: value});
        console.log(value);
        } else if (field === "pet") {
        this.setState({pet: value});
        } else {
            return
        }

  };



  changeContacts = (name,email) => {
    this.setState({
        name: name,
        email: email
    });
  }

    render(){
        return (<div>
        <form method="post" action="http://server1">
            {questions.map(question =>  <RenderQuestion onClick = {this.changeQuestion} number = {this.state.question} question = {question}/>)}
            <br/>
            <input type="submit" value="Submit"/>
        </form> </div>);
    }
};

export default Test;
