import React from 'react';
import {getVenues, getForecast, getPhotos} from './apisearch.js';


class RenderWalk extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            city : 'Riga',
            venues : [],
            forecast : {},
            searched: false,
            pic: ''
      };
      this.handleData=this.handleData.bind(this);
      this.handleClick=this.handleClick.bind(this);
      }
      handleData(e){
        console.log(e.target.value);
        const searchCity = e.target.value;
        this.setState({
            city: searchCity
        });
      }

      search(city){
        console.log('searching for ' + city);

        this.setState({
            searched: false,
            venues : [],
              })

        getVenues(city).then(vResult => {
            if (vResult !== undefined && vResult !== {}){
       this.setState({
           venues : vResult, 
           searched: true,
             })}
            else {
                this.setState({
                    venues : [],
                      })
                alert('Please try entering city again');

        }});
        //this doesn't work
        if (this.state.venues.length >1){
        console.log('getting forecast');
        getForecast(city).then(vResult =>
            {
                if(vResult.weather[0])
            {this.setState({
                forecast : vResult
                  })
                }});}
      }

      handleEnter(e){
        //e.preventDefault();
         if (e.key === 'Enter') {
             console.log('search on Enter'+ this.state.city);
           // this.search(this.state.city);
          } else {
            console.log('other key');
          }
      }

      handleClick(e){ 
        e.preventDefault();
        this.search(this.state.city);

      }

    render(){

        if(this.state.searched) {
            console.log(this.state.venues);
            return <div> 
                <input onChange={this.handleData} onKeyPress={this.handleEnter}/>
            <button onClick={this.handleClick}>Search</button>
                <RenderVenue  venues={this.state.venues} forecast = {this.state.forecast}/>
            </div>
        } else {
            return (<div>
            <input onChange={this.handleData} />
            <button onClick={this.handleClick}>Search</button>
            </div>)
    }}

};

class RenderPark extends React.Component {
    render(){
        console.log('all parks incoming')
        return (<div>
            {this.props.venues.map(park => {
                const href = 'https://maps.google.com/?q=' +park.location.lat+ ','+ park.location.lng; 
                return <div> 
                <p> {park.name}</p>
                <p> {park.categories[0].name}</p>
                <a href={href} target='_blank' >Directions</a>
                </div>
            })}
        </div>)

}}

class RenderWeather extends React.Component {
    timeConverter(UNIX_timestamp){
        const a = new Date(UNIX_timestamp * 1000);
        const hour = a.getHours();
        const min = a.getMinutes() < 10 ? '0' + a.getMinutes() : a.getMinutes(); 
        const time = hour + ':' + min;
        return time;
      }

    render(){
        console.log(this.props.weather);
        if(this.props.weather !=={} && isNaN(this.props.weather.weather[0].main)){
            const src = 'https://openweathermap.org/img/wn/'+this.props.weather.weather[0].icon+'@2x.png';
            return ( <div>
                <img src={src} />
       <p>{this.props.weather.weather[0].main}</p>
       <p>Temperature: {Math.floor(this.props.weather.main.temp- 273.15)}C</p>
       <p>Sunset {this.timeConverter(this.props.weather.sys.sunset)} PM</p>

            </div>
            )
        }else{
            return <p>Weather report is not available for this city</p>
        }
    }
};

