   //<RenderWeather weather={this.props.forecast}/>

    return <div>
    <h3>{city} </h3>

       <table>
        <tbody>
        <tr>
        <td><img src={this.state.pic} /></td>
        <td> 
       <RenderPark venues={this.props.venues}/>
        </td>
        </tr>
        </tbody>
        </table>  
    </div>
}
};
export default RenderWalk;
