import React from 'react';

let questions = [
    { title : 'Do you like warm and fuzzy pets?',
     display: true,
     type: "radio",
     name: "fuzzy",
     id: 1,
     options: [{
         idAndValue: 'yes',
         label: "Yes"
     }, {
         idAndValue: 'no',
         label: "No"
     }]},
     {title : 'What kind?',
     display: false,
     type: "radio",
     name: "pet",
     id: 2,
     options: [{
         idAndValue: 'dog',
         label: "Dog"
     }, {
         idAndValue: 'hamster',
         label: "Hamster"
     }, 
     {
         idAndValue: 'cat',
         label: "Cat"
     }]},{
     title : 'What kind?',
     display: false,
     type: "radio",
     name: "pet",
     id: 3,
     options: [{
         idAndValue: 'pig',
         label: "Pig"
     }, {
         idAndValue: 'lizard',
         label: "Lizard"
     }, 
     {
         idAndValue: 'snake',
         label: "Snake"
     }]},
     {
         title: 'What is your name?',
         display: false,
         type: 'text',
         name: 'name',
         id: 4,
         options: [{
         idAndValue: 'name',
         label: "Name"
         }]
     },
     {
         title: 'What is your email?',
         display: false,
         type: 'text',
         name: 'email',
         id: 5,
         options: [{
         idAndValue: 'emal',
         label: "Email"
         }]
     }
 ];

 class RenderQuestionOption extends React.Component {
    constructor(props) {
        super(props);
this.handleChange=this.handleChange.bind(this) 
      }

    handleChange(e) {
    console.log(e.target.value);
       console.log(e.target.name);
       // console.log(e.target);
        const value = e.target.value;
       const field = e.target.name;
      this.props.changeQuestion(field,value);
      }
     render (){
         let option = this.props.option;
         let question = this.props.question;

        return (<div>
          <input key = {question.id} onClick = {this.handleChange} type={question.type} id={option.idAndValue} name={question.name} value={option.idAndValue}  />
         <label for={option.idAndValue}>{option.label}</label> 
        </div>)
     }
 };

 class RenderOptions extends React.Component {
    render () {
        return (<div> 
            {this.props.options.options.map(option => <RenderQuestionOption changeQuestion = {this.props.changeQuestion} question = {this.props.options} option = {option}/>)}
            </div>
            )

    }
 };
 class RenderQuestion extends React.Component {
    render (){
        if (this.props.number.fur === "Notspec" && this.props.question.id === 1){
            console.log(this.props.number);
        return (<div>
        <p>{this.props.question.title}</p>
            <RenderOptions changeQuestion = {this.props.changeQuestion} options = {this.props.question}/>
            </div>
            )} else if (this.props.number.fur === "yes" && this.props.question.id === 2){
                return <div>
                <p>{this.props.question.title}</p>
                    <RenderOptions changeQuestion = {this.props.changeQuestion} options = {this.props.question}/>
                    </div>
            }
         else if (this.props.number.fur === "no" && this.props.question.id === 3){
            return <div>
            <p>{this.props.question.title}</p>
                <RenderOptions changeQuestion = {this.props.changeQuestion} options = {this.props.question}/>
                </div>
        } else if (this.props.number.pet !== "Notspec" && this.props.question.id >3 ){
            return <div>
            <p>{this.props.question.title}</p>
                <RenderOptions changeQuestion = {this.props.changeQuestion} options = {this.props.question}/>
                </div>
        } else {
            return <div></div>
        }

      }

};

class SubmitData extends React.Component {


    render(){
        console.log(this.props.data);
        if(this.props.data.fur !== 'Notspec' && this.props.data.pet !== "Notspec")
        {return (
            <input type="submit" value="Submit"/>
        )} else {
            return (
                <div></div>
            )
        }
    }
}
class Test extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          fur: 'Notspec',
          question: 1,
          pet: "Notspec",
          email: "Notspec",
          name: "Notspec",
          submitted: false
      };
        this.changeQuestion = this.changeQuestion.bind(this);
        this.changeContacts = this.changeContacts.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

      changeQuestion(field, value) {
        console.log(field);
        console.log(value);
        if(field === "fuzzy") {
        this.setState({fur: value});
        console.log(this.state.fur);
        } else if (field === "pet") {
       this.setState({pet: value});
       console.log(value);
      } else {
      console.log(value);
       };

  };

  handleSubmit(e){
    event.preventDefault();
    console.log(e.target.value);
    this.setState({
        submitted: true 
    })
  }

  changeContacts(name,email) {
   this.setState({
      name: name,
     email: email
     });
  }

    render(){
        if(this.state.submitted === false){
        return (<div>
        <form onsubmit={this.handleSubmit}>
            {questions.map(question =>  <RenderQuestion changeQuestion = {this.changeQuestion} number = {this.state} question = {question}/>)}
            <br/>
            <SubmitData data = {this.state} />
        </form> </div>);} else {
       return (<h3>Check your email for important message from {this.state.pet}s</h3>)
        }
    }
};

export default Test;
