import React from 'react';
import './game.css'

const bad = ['0x1F354','0x1F35F','0x1F355','0x1F96E','0x1F361','0x1F366','0x1F369','0x1F36A','0x1F9C1','0x1F36C'];
const good = ['0x1F957','0x1F966','0x1F96C','0x1F952','0x1F955','0x1F346','0x1F951','0x1F345','0x1F363','0x1F360'];
const location = ['one', 'two','three','four','five','six','seven','eight','nine']
const itemsNeeded = 5;


class FoodItem extends React.Component{
render(){
    const locations = this.getLocation(itemsNeeded);
    return <span role="image"  className={locations[food.indexOf(this.props.foodItem)]}>{String.fromCodePoint(this.props.foodItem)}</span>
}
}


class Gamefield extends React.Component{
    getfood(type, itemsNeeded) {
        const randomNumber = Math.floor(Math.random() * Math.floor(10));
        let food = [];
        let i =0;
        while(i<itemsNeeded){
            food.push(type[randomNumber])
            i++
        }
        return food

    }

    getLocation (itemsNeeded){
        const randomNumber = Math.floor(Math.random() * Math.floor(9));
        let locations = [];
        let i =0;
        while(i<itemsNeeded){
            locations.push(location[randomNumber])
            i++
        }
        return locations
    }

    render(){
        const food = this.getfood('good', itemsNeeded);
        return  (<div className='grid'>
            {food.map(function renderField(foodItem){
                return <FoodItem foodItem={foodItem}/>
            })}

        </div>)
    }
}

export default Gamefield;
