        //const city = $input.val();
        const urlToFetch = url + city + '&limit=50&client_id='+ clientId + '&client_secret='+clientSecret+'&v=20200225';
        fetch(urlToFetch).then(response => {
            if (response.ok) {return response.json()
            } 
              throw new Error('Request failed!');

          }, networkError => {
            console.log(networkError.message)
          }).then(response=>{
                const venues = response.response.groups[0].items.map(item => item.venue);
            console.log(venues);
            return venues
          })

            };

      getForecast = async(city) => {
        const openWeatherKey = '24baa893cee78ee225eca9d32759df14';
        const weatherUrl = 'https://api.openweathermap.org/data/2.5/weather';
        //const city = $input.val();
        const urlToFetch = weatherUrl +'?q='+city+'&APPID='+openWeatherKey;
        try{
        const response = await fetch(urlToFetch);
          if (response.ok) {
           const jsonResponse = await response.json();
            //console.log(jsonResponse);
            return jsonResponse;
          }
      }
        catch(error) {
          console.log(error)
        }
      }

};

class RenderWalk extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            city : 'Malta',
            venues : [],
            forecast : {},
            searched: false
      };
      this.handleData=this.handleData.bind(this);
      this.handleClick=this.handleClick.bind(this);
      }
      handleData(e){
        console.log(e.target.value);
        const searchCity = e.target.value;
        this.setState({
            city: searchCity
        });
      }
      handleClick(e){ 
        GetAPIDataForCity.getVenues(this.state.city).then(vResult =>
        this.setState({
            venues : vResult,
            searched: true  
              }));
        e.preventDefault();

      }
    render(){

        if(this.state.searched) {
            console.log(this.state.venues);
            return <div> 
                <h3>{this.state.city} </h3>
                <RenderVenue venues={this.state.venues}/>
            </div>
        } else {
            return <div>
            <input onChange={this.handleData} />
            <button onClick={this.handleClick}>Search</button>
            </div>
    }}

};


