        data: this.props.data.data,
    })
}


render() {
    console.log(Object.values(this.props.data['Time Series (Digital Currency Daily)']).map(value => {return value['1a. open ('+this.props.data['Meta Data']['4. Market Code']+')']}).reverse().slice(0,numberNeeded).map(
        value => {return parseFloat(value)}
    ));
    console.log(Object. keys(this.props.data['Time Series (Digital Currency Daily)']).reverse().slice(0,numberNeeded));
  //getStockdata('ETH','USD');
