import React from 'react';
import getVenues from './apisearch.js';


class RenderWalk extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            city : 'Malta',
            venues : [],
            forecast : {},
            searched: false
      };
      this.handleData=this.handleData.bind(this);
      this.handleClick=this.handleClick.bind(this);
      }
      handleData(e){
        console.log(e.target.value);
        const searchCity = e.target.value;
        this.setState({
            city: searchCity
        });
      }
      handleClick(e){ 
        console.log('searching for ' + this.state.city);
        getVenues(this.state.city).then(vResult =>
        this.setState({
            venues : vResult,
            searched: true  
              }));
        e.preventDefault();

      }
    render(){

        if(this.state.searched) {
            console.log(this.state.venues);
            return <div> 
                <h3>{this.state.city} </h3>
                <RenderVenue venues={this.state.venues}/>
            </div>
        } else {
            return <div>
            <input onChange={this.handleData} />
            <button onClick={this.handleClick}>Search</button>
            </div>
    }}

};


class RenderVenue extends React.Component{
render(){
    return <div>
        {this.props.venues[0].name}
    </div>
}
};
export default RenderWalk;
